function available($offer) {
  if($offer['attributes']['available'] == 'true') return true;
  if($offer['attributes']['available'] == 'false') return false;
  
  return true;
}

// (Рабочая) Получаем артикул товара
function newArticle($offer) {
  if (isset($offer['vendorCode'])) {
    $newArticle = $offer['vendorCode'];
    return $newArticle;
  } else return null;
}

