function name($offer) {
  if (isset($offer['name'])) {
    $newName = $offer['name'];
  } else $newName = 'Товар';
   if (isset($offer['vendorCode'])) {
    $newArticle = $offer['vendorCode'];
  } else $newArticle = null;
  return $newName . ' ' .  $newArticle;
}

function descFirst($offer) {
  $text = 'Модель ' . newArticle($offer) . ' от The North Face. Купить в интернет-магазине с доставкой. ' . category($offer)[0] . ' по супер цене.';
  return $text;
}



function descSecond($offer) {
  $categoryArray = category($offer);    
  $text = $offer['name'] . ' от легендарного бренда The North Face. Артикул представленной модели - ' . $offer['vendorCode'] . ', натуральный цвет ' . cvet($offer) . '. Концепция модели разработана специально ' . vozrast($offer) . '. В интернет-каталоге цена уже со скидкой, а также ' . end($categoryArray) . ' можно купить с доставкой. Как по Москве, так и по России. Либо забрать заказ из магазина. Обращаем внимание, что на нашем сайте только оригинальные бренды';
  return $text;
}